export default class Driver {

    position;
    points;
    wins;
    id;
    number;
    url;
    dob;
    lastName;
    nationality;
    constructors = [];

    constructor(position, points, wins, id, number, url, dob, lastName, nationality) {
        this.position = position;
        this.points = points;
        this.wins = wins;
        this.id = id;
        this.number = number;
        this.url = url;
        this.dob = dob;
        this.lastName = lastName;
        this.nationality = nationality;
    }


    static retrieveDrivers() {

        var drivers = [];

        var xmlHttp = new XMLHttpRequest();
        xmlHttp.open( "GET", "http://localhost:3333/drivers-standings", false ); // false for synchronous request
        xmlHttp.send( null );

        const obj = JSON.parse(xmlHttp.responseText);

        obj.forEach(e => {
            const newDriver = new Driver(e.PositionText, e.Points, e.Wins, e.Driver.DriverId, e.Driver.PermanentNumber, e.Driver.Url, e.Driver.DataOfBirth, e.Driver.FamilyName, e.Driver.Nationality)

            e.Constructors.forEach(constructor => {
                newDriver.constructors.push(constructor);
            });

            drivers.push(newDriver);
        });

        return drivers;
    }
}
