export default class Driver {

    position;
    points;
    wins;
    id;
    number;
    lastName;
    nationality;
    constructorid;
    constructorname;

    constructor(position, points, wins, id, number, lastName, nationality, totalrounds, constructorid, constructorname) {
        this.position = position;
        this.points = points;
        this.wins = wins;
        this.id = id;
        this.number = number;
        this.lastName = lastName;
        this.nationality = nationality;
        this.totalrounds = totalrounds;
        this.constructorid = constructorid;
        this.constructorname = constructorname;
    }


    static retrieveDrivers(year, round) {

        var drivers = [];

        var xmlHttp = new XMLHttpRequest();

        var yearString = year?.toString() || "2022"
        var roundString = round?.toString() || "22"

        xmlHttp.open( "GET", "http://localhost:3333/standings" + yearString + "-" + roundString, false ); // false for synchronous request
        xmlHttp.send( null );

        const obj = JSON.parse(xmlHttp.responseText);

        obj.forEach(e => {
            const newDriver = new Driver(e.position, e.points, e.wins, e.drivercode, e.number, e.name, e.nationality, e.totalrounds, e.constructorid, e.constructorname)
            drivers.push(newDriver);
        });

        return drivers;
    }
}
